sp_angular_provider.script:
function personalTollCheckbox( $timeout ){
	var fnLink = function($scope, elem, attrs, ngModel) {
		$timeout(
			function() {
				var el = elem.find("span.sp-editable-field span[id^='field-x_89907_vehicle_to_toll_charge-personal_toll']");
				var img = document.createElement("i");  // Create with DOM
				img.classList.add("fa");
				img.classList.add("fa-lg");
				if ($scope.ischecked == 'true') {
					img.classList.add("fa-check-square-o");
					el.text("");
					el.append(img);
				}
				else if ($scope.ischecked == 'false') {
					img.classList.add("fa-square-o");
					el.text("");
					el.append(img);
				}
				else if ($scope.ischecked) {
					img.classList.add("fa-check-square-o");
					el.text("");
					el.append(img);
				}
				else {
					img.classList.add("fa-square-o");
					el.text("");
					el.append(img);
				}
				$scope.$watch(
					function(scope) { return scope.ischecked},
					function(newValue,oldValue) {
//						if (newValue == '1') {
//							$scope.fieldmodel.value = 'true';
//							$scope.fieldmodel.stagedValue = 'true';
//						}
//						else if (newValue == '0') {
//							$scope.fieldmodel.value = 'false';
//							$scope.fieldmodel.stagedValue = 'false';
//						}
						if (newValue == 'true') {
							$(img).toggleClass("fa-check-square-o", true);
							$(img).toggleClass("fa-square-o", false);
						}
						else if (newValue == 'false') {
							$(img).toggleClass("fa-check-square-o", false);
							$(img).toggleClass("fa-square-o", true);
						}
						else if (newValue) {
							$(img).toggleClass("fa-check-square-o", true);
							$(img).toggleClass("fa-square-o", false);
						}
						else {
							$(img).toggleClass("fa-check-square-o", false);
							$(img).toggleClass("fa-square-o", true);
						}
					}
				)
			}
		);	
	};
	return {
		restrict: 'A',
		scope: {
			ischecked: "@myIsChecked",
			fieldmodel: "=myFieldModel"
		},
		link: fnLink
	};
}
