sp_rectangle_menu_item.record_script:
// only show 30 in header menu dropdown
var max = 30;

var t = data;
t.items = [];
t.count = 0;

var u = x_89907_vehicle_to.GlobalShims.getMyApprovals();

// use record watchers to tell header when to update dropdown counts
t.record_watchers = [];
t.record_watchers.push({'table':'sysapproval_approver','filter':'approverIN' + u.toString() + '^state=requested^source_table=x_89907_vehicle_to_toll_user_statement'});

var z = new GlideRecord('sysapproval_approver');
z.addQuery("approver", u);
z.addQuery("state", "requested");
z.addQuery("source_table", "x_89907_vehicle_to_toll_user_statement");
z.orderByDesc('sys_updated_on');
z.setLimit(max);
z.query();

var link = {};
link.title = gs.getMessage('View all toll approvals');
link.type = 'link';
link.href = '?id=tsp_approvals';
link.items = [];
t.items.push(link);
t.count++;
t.omitBadge = true;

var first = true; // need to offset count for badge display
while (z.next()) {
  var a = {};
  var rec = getRecordBeingApproved(z);
	if (!rec.isValidRecord())  // nothing being approved - hmmm
		continue;

  a.short_description = rec.short_description + "";
  // $sp is broken in london - maybe fixed in madrid? PRB717439
  // $sp.getRecordValues(a, z, 'sys_id,sys_updated_on');
  a.sys_id = z.getUniqueValue();
  a.sys_updated_on = z.getValue('sys_updated_on');

  // from header menu widget spDropdownTreeTemplate
  // <a ng-if="mi.type == 'record' && mi.__page" aria-label="${Open} {{::mi.number}} : {{::mi.short_description}}" aria-describedby="id_{{::mi.number}}"  ng-href="?id={{::mi.__page}}&table={{::mi.__table}}&sys_id={{::mi.sys_id}}" role="menuitem">
  a.number = rec.getDisplayValue();
  a.type = 'record';
  a.__page = 'tsp_approval';
  a.__table = z.getRecordClassName();
  t.items.push(a);
  if (!first) {
    t.count++;
  }
  first = false;
  t.omitBadge = false;
}

function getRecordBeingApproved(gr) {
  if (!gr.sysapproval.nil())
    return gr.sysapproval.getRefRecord();

  return gr.document_id.getRefRecord();
}
