sp_widget.script:
/* globals data, input, options $sp */
(function dmkRelatedDataTableFromInstance() {
	/*  "use strict"; - linter issues */
	// populate the 'data' object
	$sp.getValues(data);

	// set up the fields to show
	if (data.field_list) {
		data.fields_array = data.field_list.split(",");
	} else {
		data.field_list = $sp.getListColumns(data.table);
	}

	// set up sort order
	if (input) {
		data.o = input.o;
		data.d = input.d;
	}
	data.o = data.o || $sp.getValue("order_by");
	data.d = data.d || $sp.getValue("order_direction");

	// manage pagination
	if (input) {
		data.p = input.p;
	}
	data.p = data.p || 1;
	data.page_index = data.p - 1;
	data.window_size = $sp.getValue("maximum_entries") || 10;
	data.window_start = data.page_index * data.window_size;
	data.window_end = (data.page_index + 1) * data.window_size;

	// set up the filter/relationship
	data.filter = $sp.getValue("filter");
	data.relationship_id =
		options.relationship_id || $sp.getValue("relationship_id");
	data.apply_to = $sp.getParameter("table");
	data.apply_to_sys_id = $sp.getParameter("sys_id");
	//     data.apply_to = options.apply_to || $sp.getValue("apply_to");
	//     data.apply_to_sys_id = options.apply_to_sys_id || $sp.getValue("apply_to_sys_id");

	// validate table security
	var listItemGR = new GlideRecordSecure(data.table);
	if (!listItemGR.isValid()) {
		data.invalid_table = true;
		data.table_label = data.table;
		return;
	}

	// set up the filter - servicenow bug in widget-data-table is not updating the filter
	// after setting up the query based on the relationship so things that use the filter
	// like export to csv/pdf get ALL RECORDS!
	if (data.filter) {
		listItemGR.addEncodedQuery(data.filter);
	}
	if (data.relationship_id) {
		var relGR = new GlideRecord("sys_relationship");
		if (relGR.get(data.relationship_id)) {
			// only supports simple relationships
			var fieldRefToParent = relGR.reference_field.toString();
			var q = fieldRefToParent + "=" + data.apply_to_sys_id;
			listItemGR.addEncodedQuery(q);
		}
	}
	data.filter = listItemGR.getEncodedQuery();
	data.table_label = listItemGR.getLabel();

	var widgetParams = {
		table: data.table,
		fields: data.field_list,
		o: data.o,
		d: data.d,

		filterACLs: true,
		filter: data.filter,
		relationship_id: data.relationship_id,
		apply_to: data.apply_to,
		apply_to_sys_id: data.apply_to_sys_id,

		window_size: data.window_size,
		view: "sp",
		headerTitle: options.title,
		show_new: false,
		show_keywords: false,
		show_breadcrumbs: false,
		enable_filter: false
	};
	data.dataTableWidget = $sp.getWidget("widget-data-table", widgetParams);
})();
