sys_script.script:
(function executeRule(current, previous /* null when async */) {
	if (current == null) {
		// prevent null pointer exceptions.
		return;
	}

	if (current.company.nil()) {
		// Get the company for the transponder record based on current user
		var companyGR = new GlideRecord("core_company");
		companyGR.addQuery('primary',true);
		companyGR.addQuery('canonical',true);
		companyGR.setLimit(1);
		companyGR.query();
		current.company.setValue( companyGR.next() ? companyGR.sys_id : 'bdd47b74dbc26600f788f6e9af961937');
	}

	if (current.vendor.nil()) {
		// Get the vendor for the transponder record
		// TODO - this should come from configuration
		var vendorGR = new GlideRecord("core_company");
		vendorGR.addQuery("name","E-ZPass");
		vendorGR.query();
		if (vendorGR.next()) {
			current.vendor.setValue(vendorGR.sys_id);
		}
	}

	if (current.manufacturer.nil()) {
		// Get the model and manufacturer for the transponder record
		// TODO - this should come from configuration
		var modelGR = new GlideRecord("cmdb_hardware_product_model");
		modelGR.addQuery("name","Toll Transponder");
		modelGR.query();
		if (modelGR.next()) {
			current.model_id.setValue(modelGR.sys_id);
			if (!modelGR.manufacturer.nil()) {
				current.manufacturer.setValue(modelGR.manufacturer);
			}
		}
	}


})(current, previous);
