sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	/**
	 * @deprecated This function was moved to new payment wizard ui pages
	 */

	// todo deprecated? replaced by new payment functionality?

current.setValue('internal_mark_paid',false);
var gdt = new GlideDateTime(); 

var currentPersonalAmount = current.getValue('amount');
var currentUser = current.getValue('user');
var currrentCheckAmount = current.getValue('check_amount');
var currentCheckNumber = current.getValue('check_number');
var currentCheckDate = current.getValue('check_date');

var gd = new GlideRecord('x_89907_vehicle_to_toll_user_statement');
gd.addQuery('payment', '=', current.sys_id);
gd.query();
var totalUserStatements = gd.getRowCount();

if (totalUserStatements == 0)
 {
  gs.addErrorMessage("There were no user statements located");
  current.setAbortAction(true);	 
 }
else
 {
  while (gd.next())
   {
    gd.setValue('progress_phase',7);
    gd.setValue('status',2);
    gd.setValue('check_number',currentCheckNumber);
    gd.setValue('check_date',currentCheckDate);
    gd.setValue('check_amount',currrentCheckAmount);
    gd.setValue('closed_by',gs.getUserID());
    gd.setValue('closed_on',gdt);
    gd.setValue('resolution_comments',"User statement closed using the Mark paid action");
    gd.update();
   }

  current.setValue('phase',3);
  current.setValue('status',2);
  current.setValue('closed_by',gs.getUserID());
  current.setValue('closed_on',gdt);
  current.setValue('resolution_comments',"Payment closed using the Mark paid action");
  //current.update();

  gs.addInfoMessage("There were a total of " + totalUserStatements + " user statements closed and paid.");

 }


})(current, previous);
