sys_script_client.script:
function onLoad() {
   //Type appropriate comment here, and begin script below
   enableOrDisableBulkReassign();
}

  function enableOrDisableBulkReassign() {
    var bulkReassignBtn = document.getElementById('sysverb_bulk_reassign_transponders');
    if (!bulkReassignBtn) return;

    bulkReassignBtn.disabled = true;
    g_list.setSubmitValue('sysverb_bulk_reassign_transponders', '');

    g_list.on('checkbox_change', function() {
      var checkedItems = g_list.getChecked();
      bulkReassignBtn.disabled = checkedItems === '';
      g_list.setSubmitValue('sysverb_bulk_reassign_transponders', checkedItems);
    });
  }
