sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading) {
		return;
	}

	// get the values
	var shiftStartTimeStr = newValue;
	var shiftEndTimeStr = g_form.getValue("shift_end");

	// we're revalidating everything so hide all the messages
	g_form.hideFieldMsg("shift_start", true);
	g_form.hideFieldMsg("shift_end", true);

	var shiftStart;
	if (shiftStartTimeStr) {
		// get the object
		shiftStart = getDateObjectForTimeString(shiftStartTimeStr);
		// if it's invalid put up a message
		if (shiftStart === 0) {
			g_form.showFieldMsg(
				"shift_start",
				"Invalid time",
				"error"
			);
		}
	}

	var shiftEnd;
	if (shiftEndTimeStr) {
		// get the object
		shiftEnd = getDateObjectForTimeString(shiftEndTimeStr);
		// if it's invalid put up a message
		if (shiftEnd === 0) {
			g_form.showFieldMsg(
				"shift_end",
				"Invalid time",
				"error"
			);
		}
	}


	if (!shiftStart || shiftStart === 0 || !shiftEnd || shiftEnd === 0) {
		return;
	}

	if (shiftStart > shiftEnd) {
		// warn for overnight shift
		g_form.showFieldMsg(
			"shift_end",
			getMessage("Toll Configuration On Submit-Toll Configuration-1"),
			"warning"
		);
	} else if (shiftStart == shiftEnd) {
		// error for equal shifts
		g_form.showFieldMsg(
			"shift_end",
			getMessage("Toll Configuration On Submit-Toll Configuration-2"),
			"error"
		);
	}

	function getDateObjectForTimeString(timeString) {
		// Get the epoch base in the user date format
		// We are only concerned about relative times so the actual date doesn't matter
		var userDateFormat = g_user_date_format;
		var baseDateStr = formatDate(new Date(1970,0,1), userDateFormat);

		// Create a date/time string in user date time format
		// var userTimeFormat = g_user_date_time_format.substring(g_user_date_format.length + 1);
		var fieldDateTimeStr = baseDateStr + " " + timeString;

		// Return a date object or 0
		return getDateFromFormat(fieldDateTimeStr, g_user_date_time_format);
	}

}
