sys_script_client.script:
/* exported onChange */
/**
 * @description Clear values/apply defaults when the status changes to Retired
 * @param {*} control
 * @param {string} oldValue
 * @param {string} newValue
 * @param {boolean} isLoading
 * @param {boolean} isTemplate
 */
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	var tdayDate = formatDate(new Date(),g_user_date_time_format);
	switch (newValue) {
		case "1":
			// Changed to Installed
			g_form.clearValue('u_removed_date');
			g_form.setValue('install_date', tdayDate);
			g_form.setValue('u_create_invoice', true);
			break;
		case "6":
			// Changed to In Stock
			g_form.clearValue('install_date');
			g_form.clearValue('u_removed_date');
			g_form.clearValue('assigned_to');
			g_form.clearValue('u_vehicle_number');
			g_form.clearValue('u_plate_number');
			g_form.setValue('delivery_date', tdayDate);	
			g_form.setValue('u_create_invoice', false);
			break;
		case "7":
		case "8":
			// Changed to Retired or Stolen
			g_form.setValue('u_removed_date', tdayDate);
			g_form.setValue('u_create_invoice', false);
			break;
		default:
	}
}
