sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

    template.print('<p><font size="5" color="#808080" face="helvetica"><strong>');
	template.print(gs.getMessage('You are receiving this email because your payment for toll statement is due'));
	template.print('</strong></font></p>');

	template.print(gs.getMessage('<p>The following is a list of your statements now due:</p>'));
	template.print(gs.getMessage('<ul>'));

	var gr = new GlideRecord("x_89907_vehicle_to_toll_user_statement");
	gr.addQuery('status','1');
	gr.addQuery('progress_phase','2');
	gr.addQuery('user',current.user);
    gr.query();
    while(gr.next()) 
	 {
      template.print('<li>' + gr.transponder_number + "-" + gr.period_begin_date + "-" + gr.period_end_date + "-" + gr.personal_amount + "</li>");
     }
	template.print(gs.getMessage('</ul>'));


})(current, template, email, email_action, event);
