sys_script_email.script:
(function runMailScript(current, template, email, email_action, event) {
	var m2mGR = new GlideRecord("x_89907_vehicle_to_m2m_users_toll_approvers");
	m2mGR.addQuery("user",current.user);
	m2mGR.query();
	while(m2mGR.next()) {
		var approvalGroupGR = new GlideRecord("x_89907_vehicle_to_toll_approver");
		approvalGroupGR.addQuery("sys_id",m2mGR.toll_approver);
		approvalGroupGR.addQuery("active",true);
		approvalGroupGR.query();
		if(approvalGroupGR.next()) {
			var manager = approvalGroupGR.manager;
			if (!manager.email.nil() && manager.notification == "2") {
				email.addAddress("cc", manager.email.toString(), manager.getDisplayValue());
			}
		}
	}
})(current, template, email, email_action, event);
