sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

	template.print(gs.getMessage('<ul>'));


	var approverGR = new GlideRecord("sysapproval_approver");
	approverGR.addQuery('source_table',"=","x_89907_vehicle_to_toll_user_statement");
	approverGR.addQuery('state',"IN","requested,rejected");
	approverGR.addQuery('approver',"=",current.approver.sys_id);
	var joinCondition = approverGR.addJoinQuery('x_89907_vehicle_to_toll_user_statement','document_id','sys_id');
	joinCondition.addCondition("approval_set","<","javascript:gs.daysAgoStart(30)");
	approverGR.query();

	while(gr.next()) 
	 {
		 var statementGR = new GlideRecord("x_89907_vehicle_to_toll_user_statement");
		 statementGR.get(approverGR.document_id);
		 template.print('<li>' + approverGR.state + "-" + statementGR.approval_set + "-" + statementGR.getDisplayValue() + "</li>");
     }
	template.print(gs.getMessage('</ul>'));


})(current, template, email, email_action, event);
