sys_script_fix.script:
var role = 'approver_user';

var gr = new GlideRecord('x_89907_vehicle_to_toll_approver');
gr.addEncodedQuery('active=true');
gr.query();
while (gr.next())
{
if(!userHasRole(gr.manager, role)){
	gs.addInfoMessage('This user has no role' + gr.manager.getDisplayValue());
}

}

function userHasRole(userId, role){

	var urec = new GlideRecord('sys_user_has_role');
	urec.addQuery('user', userId);
	urec.addQuery('role.name', role);
	urec.query();

	return urec.hasNext();

}
