sys_script_include.script:
var DMKUtil = Class.create();
DMKUtil.prototype = {
	initialize: function() {},

	type: "DMKUtil"
};

DMKUtil.glideRecordSetToJSON = function glideRecordSetToJSON(
	anyGR,
	includeSys
) {
	var arr = DMKUtil.glideRecordSetToObjectArray(anyGR, includeSys);
	return JSON.stringify(arr, null, "\t");
};
DMKUtil.glideRecordToJSON = function glideRecordToJSON(anyGR, includeSys) {
	var obj = DMKUtil.glideRecordToObject(anyGR, includeSys);
	return JSON.stringify(obj, null, "\t");
};

DMKUtil.glideRecordToObject = function glideRecordToObject(anyGR, includeSys) {
	var obj = {};
	// eslint-disable-next-line no-restricted-syntax
	for (var prop in anyGR) {
		if (includeSys || !/^sys_/.test(prop)) {
			try {
				obj[prop] = anyGR[prop].toString();
			} catch (err) {
				// noop - ignore error
			}
		}
	}
	return obj;
};

DMKUtil.glideRecordSetToObjectArray = function glideRecordSetToObjectArray(
	anyGR,
	includeSys
) {
	var arr = [];
	while (anyGR.next()) {
		arr.push(DMKUtil.glideRecordToObject(anyGR, includeSys));
	}
	return arr;
};
