sys_script_include.script:
var Utilities = Class.create();
Utilities.prototype = {
	initialize: function initialize() {
	},

	DuplicateCheck: function DuplicateCheck(current, column) {
		var table = current.getTableName();
		var value = current.getValue(column);
		var operation = current.operation();
		var sysid = current.sys_id;

		var duplicateGR = new GlideRecord(table);
		duplicateGR.addQuery(column, "=", value);
		if (operation == "update") {
			duplicateGR.addQuery("sys_id", "!=", sysid);
		}
		duplicateGR.query();

		return duplicateGR.hasNext();
	},
	type: "Utilities"
};
