sys_transform_entry.source_script:
answer = (function transformEntry(source) {

	var minGDT;
	if (!source.u_installed_date.nil()) {
		var installDate = new GlideDateTime();
		installDate.setDisplayValue(source.u_installed_date + " 12:00:00", "dd-MMM-yy HH:mm:ss"); //uses current user session time zone
		minGDT = installDate;
	}

	if (!source.u_date_received.nil()) {
		var receivedDate = new GlideDateTime();
		receivedDate.setDisplayValue(source.u_date_received + " 12:00:00", "dd-MMM-yy HH:mm:ss"); //uses current user session time zone
		if (minGDT) {
			minGDT = (minGDT.before(receivedDate)) ? minGDT : receivedDate;
		} else {
			minGDT = receivedDate;
		}
	}

	if (!source.u_date_of_status.nil()) {
		var statusDate = new GlideDateTime();
		statusDate.setDisplayValue(source.u_date_of_status + " 12:00:00", "dd-MMM-yy HH:mm:ss"); //uses current user session time zone
		if (minGDT) {
			minGDT = (minGDT.before(statusDate)) ? minGDT : statusDate;
		} else {
			minGDT = statusDate;
		}
	}

	if (!minGDT) {
		return "";
	}

	var gd = new GlideDate(minGDT.getDate());
	return gd.getByFormat("yyyy-MM-dd"); // must match date format specified above

})(source);
