sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/ ) {

// The onBefore event script is processed at the start of a row transformation, before the source row is transformed into the target row


// Skip reserved agnecy id's
var id = source.u_agency_id.toString();
var description = source.u_description.toString();
if ((id.length > 3 ) || (description.toUpper().startsWith("RESERVED"))) {
	ignore = true;
	info ="Reserved/invalid agency id, row ignored: " + id;
	log.info( info );
}
})(source, map, log, target);
