sys_ui_action.script:
/* exported setBatchStatusValidated */
// Client-side 'onclick' function
function setBatchStatusValidated() {
	// Perform any checks on the client before we proceed with server processing
	var answer = confirm("Changing the status does not remove records generated by previous processing and should only be used for error recovery. Are you sure you want to change the status?");
	if (answer != true) {
		return false;  // Abort submission
	}
	// Call the UI Action and skip the 'onclick' function
	gsftSubmit(null, g_form.getFormElement(), 'setBatchStatusValidated'); // MUST call the 'Action name' set in this UI Action
	return true;
}

// Code that runs without 'onclick'
// Ensure call to server-side function with no browser errors
if (typeof window == "undefined") {
	runBusRuleCode();
}

// Server-side function
function runBusRuleCode() {
	var stmt = new GlideRecord("x_89907_vehicle_to_toll_import_statement");
	stmt.addQuery("toll_import_batch", current.sys_id);
	stmt.query();
	while (stmt.next()) {
		stmt.statement = "NULL";
		stmt.status = "4"; // validated
		stmt.update();
	}
	current.status = "4";
	current.update();
	gs.addInfoMessage("Batch status reset to validated.");
	action.setRedirectURL(current);
}
