sys_ui_action.script:
/* exported setUserStatementCancel */
// Client-side 'onclick' function
function setUserStatementCancel() {
	// Perform any checks on the client before we proceed with server processing
	g_form.hideAllFieldMsgs();

	// Comment is required to cancel a statement - prompt and then
	// abort if one is not provided
	var con = prompt(
		getMessage("Cancel user statement-Toll User Statement-100-1")
	);
	if (con == "" || con == null) {
		return false;
	}
	g_form.setValue("status_notes", con);

	// Call the UI Action and skip the 'onclick' function
	gsftSubmit(null, g_form.getFormElement(), "Cancel user statement"); // MUST call the 'Action name' set in this UI Action
	return undefined;
}

// Code that runs without 'onclick'
// Ensure call to server-side function with no browser errors
if (typeof window == "undefined") {
	runBusRuleCode();
}

// Server-side function
function runBusRuleCode() {
	// Cancel any approval workflows for this statement
	var wf = new global.Workflow();
	wf.cancel(current);
	var awf = new global.WorkflowApprovalUtils();
	awf.cancelAll(
		current,
		"Approval workflow canceled by " + gs.getUserDisplayName()
	);
	current.approval = "cancelled"; // Cancelled
	current.approval_set = "";

	// Update all the Closed/Cancelled fields
	current.status = 2; // Closed
	current.progress_phase = 3; // Cancelled
	current.closed_on = new GlideDateTime();
	current.closed_by = gs.getUserID();
	// todo - not adding status note? it was commented out

	// Queue message for the user who initiated
	var msgArray = [];
	msgArray.push(current.number);
	msgArray.push(current.user.getDisplayValue());
	gs.addInfoMessage(
		gs.getMessage("Cancel user statement-Toll User Statement-100-2", msgArray)
	);

	// We're done - save it
	current.update();
}
