sys_ui_action.script:
/* exported runClientReValidateCode */
// Client-side 'onclick' function
function runClientReValidateCode() {
	// Perform any checks on the client before we proceed with server processing
	// Return false to Abort submission

	// Call the UI Action and skip the 'onclick' function
	gsftSubmit(null, g_form.getFormElement(), "Re-validate Toll Data Import"); // MUST call the 'Action name' set in this UI Action
}

// Code that runs without 'onclick'
// Ensure call to server-side function with no browser errors
if (typeof window == "undefined") {
	runBusRuleCode();
}

// Server-side function
function runBusRuleCode() {
	var exGr = new GlideRecord("x_89907_vehicle_to_toll_import_exception");
	exGr.addQuery("import_batch", current.sys_id);
	exGr.query();
	exGr.setValue("status", "2"); // set status "Inactive"
	exGr.updateMultiple();

	var stmt = new GlideRecord("x_89907_vehicle_to_toll_import_statement");
	stmt.addQuery("toll_import_batch", current.sys_id);
	stmt.query();
	var stmtCount = 0;
	// update the statements to validating so trigger waits once batch is updated
	while (stmt.next()) {
		stmtCount++;
		stmt.status = "3"; // validating
		stmt.update();
	}
	if (stmtCount > 0) {
		// update the batch so trigger fires
		current.status = "3";
		current.update();
		stmt = new GlideRecord("x_89907_vehicle_to_toll_import_statement");
		stmt.addQuery("toll_import_batch", current.sys_id);
		stmt.query();
		// schedule the jobs after updating batch so statements aren't complete before flow gets to trigger
		while (stmt.next()) {
			gs.info("Adding validate statement event to queue for {0}", stmt.name);
			var parm1 = gs.getUserID();
			var parm2 = "parm2";
			gs.eventQueue("x_89907_vehicle_to.import.validate", stmt, parm1, parm2);
		}
		gs.addInfoMessage(
			gs.getMessage("Re-validate Toll Data Import-Toll Import Batch-300-1")
		);
	} else {
		gs.addInfoMessage(
			gs.getMessage("Re-validate Toll Data Import-Toll Import Batch-300-2")
		);
	}

	action.setRedirectURL(current);
}
