// Function to enable or disable the "Bulk Reassign Transponders" button based on the transponders selected in the list view
function enableOrDisableBulkReassign() {
  var selected = g_list.getSelected(); // Get the selected transponders in the list view
  var bulkReassignBtn = document.getElementById('bulk_reassign_transponders'); // Get the "Bulk Reassign Transponders" button element by its ID

  // If at least one transponder is selected, enable the "Bulk Reassign Transponders" button
  if (selected) {
    bulkReassignBtn.removeAttribute('disabled');
  } else {
    // If no transponders are selected, disable the "Bulk Reassign Transponders" button
    bulkReassignBtn.setAttribute('disabled', 'true');
  }
}

// Function to initialize the button state and register the event listener for the list view
function initializeDisableBulkReassign() {
  var bulkReassignBtn = document.getElementById('bulk_reassign_transponders'); // Get the "Bulk Reassign Transponders" button element by its ID
  bulkReassignBtn.setAttribute('disabled', 'true'); // Initially disable the "Bulk Reassign Transponders" button

  // Register the 'change' event listener on the list view to call the 'enableOrDisableBulkReassign' function
  g_list.on('change', enableOrDisableBulkReassign);
}

// Call the 'initializeDisableBulkReassign' function when the page loads
addLoadEvent(initializeDisableBulkReassign);
