sysauto_script.condition:
// Get the day of week. 1=Monday, 7=Sunday
var now = new GlideDateTime();

// Run only on weekdays
now.getDayOfWeekLocalTime() < 6;

sysauto_script.script:
gs.info("Starting Vehicle Tolls Manager Snapshot scheduled job");

// Get the relationship records from the M2M table grouped by toll approval group
var approverUsersM2MGAgg = new GlideAggregate("x_89907_vehicle_to_m2m_users_toll_approvers");
approverUsersM2MGAgg.orderBy("toll_approver");
approverUsersM2MGAgg.groupBy("toll_approver");
approverUsersM2MGAgg.query();

// Iterate the approval groups and queue the snapshot notification event
while (approverUsersM2MGAgg.next()) {
	var approverGR = new GlideRecord("x_89907_vehicle_to_toll_approver");
	if (approverGR.get(approverUsersM2MGAgg.toll_approver.sys_id)) {
		gs.eventQueue("x_89907_vehicle_to.manager.snapshot", approverGR, approverGR.manager, "");
	}
}
