sysauto_script.condition:
// Get the day of week. 1=Monday, 7=Sunday
var now = new GlideDateTime();

// Run only on weekdays
now.getDayOfWeekLocalTime() < 6;

sysauto_script.script:
gs.info("Starting User Statements Requiring Approval scheduled job");

//-------------------------------------------------------------------------------------
// Part 1 -- Locate all the approvals for user statements in requested state
//-------------------------------------------------------------------------------------
var logTotals = false;
var logEach = true;

//-------------------------------------------------------------------------------------
// Find all user statements that are OPEN/PAYMENTOWED/APPROVED and group them
//-------------------------------------------------------------------------------------

var approvalGAgg = new GlideAggregate("sysapproval_approver");
approvalGAgg.addQuery("state", "=", "requested");
approvalGAgg.addQuery(
	"source_table",
	"=",
	"x_89907_vehicle_to_toll_user_statement"
);
approvalGAgg.addAggregate("COUNT");
approvalGAgg.groupBy("approver");
approvalGAgg.query();
var totalPotential = approvalGAgg.getRowCount();
//-------------------------------------------------------------------------------------
// Get each approval and notify
//-------------------------------------------------------------------------------------
var totalProcessed = 0;
while (approvalGAgg.next()) {
	var approverSysID = approvalGAgg.approver;
	var approverDisplayValue = approvalGAgg.approver.getDisplayValue();
	var groupApprovalCount = approvalGAgg.getAggregate("COUNT");
	if (logEach) {
		gs.info("User={0} ,Count={1}", approverDisplayValue, groupApprovalCount);
	}
	totalProcessed++;
	gs.eventQueue(
		"x_89907_vehicle_to.statement.requires.ap",
		current,
		approverSysID,
		groupApprovalCount
	);
}

if (logTotals) {
	gs.info('Total processed: {0}\nTotal          : {1}', totalProcessed, totalPotential);
}
