// Set the variable 'answer' to a comma-separated list of user ids and/or group ids or an array of user/group ids to add as approvers.
//
// For example:
//       answer = [];
//       answer.push('id1');
//       answer.push('id2');

answer = [];
var sgr = new GlideRecord("x_89907_vehicle_to_m2m_users_toll_approvers");
sgr.addQuery("user",current.user);
sgr.query();
while(sgr.next())
 {
  var zgr = new GlideRecord("x_89907_vehicle_to_toll_approver");
  zgr.addQuery("sys_id",sgr.toll_approver);
  zgr.addQuery("active",true);
  zgr.query();
  if(zgr.next())
   {
    answer.push(zgr.manager);	   
   }     
  else;
 }

