/**
* Set the answer variable to 'yes' if configuration indicates approval
* If we don't have everything we need to find configuration or 
* statement doesn't match configuration approval criteria, return 'no'
*/
answer = configurationRequiresApproval() ? 'yes' : 'no';

function configurationRequiresApproval() {
	// Verify we have everything we need to find the configuration
	if (current == null || !current.transponder || !current.transponder.company) {
		return false;
	}
	// Find an active configuration for the transponder's company
	var tollConfigGR = new GlideRecord("x_89907_vehicle_to_toll_configuration");
	tollConfigGR.addQuery("active",true);
	tollConfigGR.addQuery("company",current.transponder.company);
	tollConfigGR.query();
	if (!tollConfigGR.next()) {
		return false;
	}
	// Test the configuration approval criteria against the statement's phase
	var approvalCriteria = tollConfigGR.user_statement_approval.toString();
	if (approvalCriteria == 'all') {
		return true;
	}
	if (current.phase == '0' && approvalCriteria == 'acknowledged') {
		return true;
	}
	if (current.phase == '2' && approvalCriteria == 'paymentOwed') {
		return true;
	}
	return false;
}

